import React, { Component } from 'react'
import './estilo.css';


class Table extends Component {
   constructor(props) {
      super(props) //since we are extending class Table so we have to use super in order to override Component class constructor
      console.log("aaa", props.json)
      var lista = [
         { id: 1, name: 'Wasif', age: 21, email: 'wasif@email.com' },
         { id: 2, name: 'Ali', age: 19, email: 'ali@email.com' },
         { id: 3, name: 'Saad', age: 16, email: 'saad@email.com' },
         { id: 4, name: 'Asad', age: 25, email: 'asad@email.com' }
      ];
      const lista2 = props.json.map(item => ({
         quantidade: item[0],
         tempo: item[1],
         rendimento_cdb: item[2],
         rendimento_poupanca: [3],
         email: item[4] 
      }))
      console.log(lista2)
      this.state = { //state is by default an object
         students: lista2
      }
   }

   renderTableData() {
      return this.state.students.map((student, index) => {
      const { quantidade, tempo, rendimento_poupanca, rendimento_cdb, email } = student //destructuring
      return (
         <tr key={email}>
               <td>{quantidade}</td>
               <td>{tempo}</td>
               <td>{rendimento_poupanca}</td>
               <td>{rendimento_cdb}</td>
         </tr>
      )
      })
    }

    renderTableHeader() {
        let header = Object.keys(this.state.students[0])
        return header.map((key, index) => {
           return <th key={index}>{key.toUpperCase()}</th>
        })
    }

    render() {
        return (
           <div>
              <h1 id='title'>React Dynamic Table</h1>
              <table id='students'>
                 <tbody>
                    <tr>
                       <th>Investimento inicial</th>
                       <th>Tempo de investimento</th>
                       <th>Rendimento na poupan√ßa</th>
                       <th>Rendimento no CDB</th>
                    </tr>
                    {this.renderTableData()}
                 </tbody>
              </table>
           </div>
        )
     }
}



export default Table //exporting a component make it reusable and this is the beauty of react
