import react, { Component } from 'react'

class PostForm extends Component{
    

    async postData(nome, email, telefone, senha, quantidade, tempo){
        if (nome !== '' && email !== '' && telefone !== '' && senha !== '' && quantidade !== '' && tempo !== ''){
            try{
            const response = await fetch('http://localhost:5000/simulacao', {
                method: 'post',
                headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                nome: nome,
                email: email,
                telefone: telefone,
                senha_acesso: senha,
                quantidade: quantidade,
                tempo_investimento: tempo
                })
            }).then(response => response.json());
            return (response)
            }catch(e){
            console.log(e)
            }
        }
        else{
            alert("preencha todos os campos")
        }
      }
    
    load_graph(){
        return(
            <div id="chart"></div>
        )
    }

    constructor(props){
        super(props)
        
        this.state = {
            nome: '',
            telefone: '',
            email: '',
            senha: '',
            quantidade: '',
            tempo: ''
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = async e => {
        e.preventDefault()
        console.log(this.state)
        const response = await this.postData(this.state.nome, this.state.email, this.state.telefone, this.state.senha, this.state.quantidade, this.state.tempo)
        this.setState({json_response: response})
        console.log(response)
    }

    render(){
        const {nome, telefone, email, senha, quantidade, tempo} = this.state
        return(
            <div>
                <form onSubmit={this.submitHandler}>
                    <div>
                        <input type='text' name='nome' value={nome} onChange={this.changeHandler}/>
                    </div>
                    <div>
                        <input type='text' name='telefone' value={telefone} onChange={this.changeHandler}/>
                    </div>
                    <div>
                        <input type='text' name='email' value={email} onChange={this.changeHandler}/>
                    </div>
                    <div>
                        <input type='text' name='senha' value={senha} onChange={this.changeHandler}/>
                    </div>
                    <div>
                        <input type='text' name='quantidade' value={quantidade} onChange={this.changeHandler}/>
                    </div>
                    <div>
                        <input type='text' name='tempo' value={tempo} onChange={this.changeHandler}/>
                    </div>
                    <button type='submit'>Submit</button>
                    <div class="container">
                        <div class="col-md-12"></div>
                        <div class="op1"><object type="text/html" data="graph.html"></object></div>
                        <div class="op2">Opção 2</div>
                    </div>
                </form>
            </div>
        )
    }
}

export default PostForm
