import react, { Component } from 'react'

class PostForm extends Component{
    async postData(email){
        if (email !== ''){
            try{
            const response = await fetch('http://localhost:5000/consulta_simulacoes', {
                method: 'post',
                headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                email: email,
                })
            }).then(response => response.json());
            return (response)
            }catch(e){
            console.log(e)
            }
        }
        else{
            alert("preencha todos os campos")
            return false
        }
      }
    
    constructor(props){
        super(props)
        
        this.state = {
            email: '',
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = async e => {
        e.preventDefault()
        const response = await this.postData(this.state.email)
        console.log(response)
    }

    render(){
        const {nome, telefone, email, senha, quantidade, tempo} = this.state
        return(
            <div>
                <form onSubmit={this.submitHandler}>
                    <div>
                        <input placeholder="Digite seu nome" type='text' name='nome' value={nome} onChange={this.changeHandler}/>
                    </div>
                    <button class='button' type='submit'>Submit</button>
                    <div class="container">
                    </div>
                </form>
            </div>
        )
    }
}



export default PostForm
