import react, { Component } from 'react'

class PostForm extends Component{

    async postData(nome, email, telefone, senha_acesso, quantidade, tempo_investimento){

        try{
          const response = await fetch('http://localhost:5000/simulacao', {
            method: 'post',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              nome: nome,
              email: email,
              telefone: telefone,
              senha_acesso: senha_acesso,
              quantidade: quantidade,
              tempo_investimento: tempo_investimento
            })
          }).then(response => response.json());
          console.log(response)
        }catch(e){
          console.log(e)
        }
      }
    
    constructor(props){
        super(props)
        
        this.state = {
            nome: '',
            telefone: '',
            numero: ''
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        const response = this.postData()
        console.log(this.state.nome)
    }

    render(){
        const {nome, telefone, email} = this.state
        return(
            <div>
                <form onSubmit={this.submitHandler}>
                    <div>
                        <input type='text' name='nome' value={nome} onChange={this.changeHandler}/>
                    </div>
                    <div>
                        <input type='text' name='telefone' value={telefone} onChange={this.changeHandler}/>
                    </div>
                    <div>
                        <input type='text' name='email' value={email} onChange={this.changeHandler}/>
                    </div>
                    <button type='submit'>Submit</button>
                </form>
            </div>
        )
    }
}

export default PostForm
